import React, {Component,useState} from "react";


function App () {

        var realTime = new Date().toLocaleTimeString();
       const Xtime=()=>{
        return(setInterval(realTime,1000))

       }


        const greetStyle = {
            backgroundColor:'blue'
        }

        let time =new Date().getHours();
        let greeting = '';
            if (time<12){
                greeting = 'Good Morning';
                greetStyle.color='green';
            }
            else if(time>=12 && time<16){
                greeting = 'Good Afternoon';
                greetStyle.color="red"
            }
            else if (time>=16 && time <19){
                greeting = "Good Evening"
                greetStyle.color='yellow'
            }
            else{
                greeting = 'Good Night';
                greetStyle.color="black"
            }
        return(
            <>
            <div className="parent">
            <h1 className='heading'>Welcome to React Time App</h1>
            <h1 className='myname'>Made By ðŸ’– Vikash Chaurasia</h1>
            <h1 className="time">{realTime}</h1>
            <></>
            <div className="child">
            <h1 className="hsir">Hello Sir , <span className="greet" style={greetStyle}>{greeting}</span></h1>
            </div>
            </div>
            </>
        )

};

export default App ;
