import React, {Component} from "react";


function App () {

        // var realTime = new Date().toLocaleTimeString();
        // setInterval('${realTime}',1000)


        class App extends Component {
            constructor() {
              super();
              this.state = { time: new Date() };
            }
            currentTime() {
              this.setState({ time: new Date() });
            }
            componentDidMount() {
              this.interval = setInterval(() => this.currentTime(), 1000);
            }
            componentWillUnmount() {
              clearInterval(this.interval);
            }
            render() {
              return (
                <>
                  <div className="Clock">

                  </div>
                </>
              );
            }
          }


        const greetStyle = {
            backgroundColor:'blue'
        }

        let time =new Date().getHours();
        let greeting = '';
            if (time<12){
                greeting = 'Good Morning';
                greetStyle.color='green';
            }
            else if(time>=12 && time<16){
                greeting = 'Good Afternoon';
                greetStyle.color="red"
            }
            else if (time>=16 && time <19){
                greeting = "Good Evening"
                greetStyle.color='yellow'
            }
            else{
                greeting = 'Good Night';
                greetStyle.color="black"
            }
        return(
            <>
            <div className="parentDiv">
            {/* <h1>{realTime}</h1> */}
            <h3 id="time">{this.state.time.toLocaleTimeString()}</h3>
            <h1>Hello Sir , <span className="greet" style={greetStyle}>{greeting}</span></h1>
            </div>
            </>
        )

};

export default App ;
